                          Verifier's ID*
                        </label>
                        <Form.Control
                          type="text"
                          className="form-control mt-2"
                          id="exampleVerifierID"
                          placeholder="Id"
                          onChange={this.id}
                          value={this.state.VerifiersID}
                          required
                        />
                      </Form.Group>
                      <Form.Group>
                        {!this.state.customSource ? (
                          <div>
                            <label
                              htmlfor="inputState"
                              className="mt-4 mx-1 mb-2"
                            >
                              Source*
                            </label>
                            <div>
                              <label
                                htmlfor="inputState"
                                className="mt-2 mx-2 mb-2"
                              >
                                Passbook
                              </label>
                              <input
                                type="radio"
                                name="source"
                                value="Passbook"
                                onClick={(e) => {
                                  this.setState({ Source: e.target.value });
                                  console.log(e.target.value);
                                }}
                              />
                              <label
                                htmlfor="inputState"
                                className="mt-2   mx-2 mb-2"
                              >
                                VoterId
                              </label>

                              <input
                                type="radio"
                                name="source"
                                value="VoterId"
                                onClick={(e) => {
                                  this.setState({ Source: e.target.value });
                                  console.log(e.target.value);
                                }}
                              />
                              <label
                                htmlfor="inputState"
                                className="mt-2   mx-2 mb-2"
                              >
                                PANCard
                              </label>
                              <input
                                type="radio"
                                name="source"
                                value="PANCard"
                                onClick={(e) => {
                                  this.setState({ Source: e.target.value });
                                  console.log(e.target.value);
                                }}
                              />
                              <label
                                htmlfor="inputState"
                                className="mt-2  mx-2 mb-2"
                              >
                                Others
                              </label>

                              <input
                                type="radio"
                                name="source"
                                value=""
                                onClick={(e) => {
                                  this.setState({ Source: e.target.value });
                                  this.setState({ customSource: true });
                                }}
                              />
                            </div>
                          </div>
                        ) : (
                          <Form.Group>
                            <label
                              htmlFor="exampleInputEmail3 mt-2"
                              className="mt-2"
                            >
                              Enter Custom Source*
                            </label>
                            <Form.Control
                              type="text"
                              className="form-control mt-2"
                              id="exampleVerifierID"
                              placeholder="Enter Custom Source"
                              onChange={(e) =>
                                this.setState({ Source: e.target.value })
                              }
                              value={this.state.Source}
                              required
                            />
                          </Form.Group>
                        )}
                      </Form.Group>

                      <hr />
                      <label>Admitted Signature*</label>
                      <div className="custom-file mt-2">
                        <Form.Control
                          type="file"
                          className="form-control visibility-hidden mt-2"
                          id="customFileLang"
                          lang="es"
                          onChange={this.onFileChange1}
                          required
                        />
                      </div>
                      <label className="mt-2">Questioned Signature*</label>
                      <div className="custom-file mt-2">
                        <Form.Control
                          type="file"
                          className="form-control visibility-hidden mt-2"
                          id="customFileLang"
                          lang="es"
                          onChange={this.onFileChange2}
                          required
                        />
                      </div>
                    </Form.Group>

                    <button
                      type="submit"
                      className="btn btn-primary mr-2 mt-4"
                      // onClick={this.onFileUpload}
                    >
                      Submit
                    </button>
                    <button className="btn btn-light mx-4 mt-4">Cancel</button>
                  </Form>
                  {/* </form> */}
                </div>
              </div>
              {this.state.photo1 && (
                <div className="sign__con1 d-flex flex-column mx-1" id="photo1">
                  <div className="">
                    <h4 className="mb-4">Admitted Signature</h4>
                    <img
                      className="col-8"
                      src={this.state.displayimage1}
                      alt=""
                    />
                  </div>
                </div>
              )}
              {this.state.photo2 && (
                <div className="sign__con1 d-flex flex-column mx-1" id="photo2">
                  <div className="">
                    <h4 className="mb-4">Questioned Signature</h4>
                    <img
                      className="col-8"
                      src={this.state.displayimage2}
                      alt=""
                    />
                  </div>
                </div>
              )}
            </div>
          </div>
        )}
        {this.state.Trans && <Transition fileInfo={this.state.formInfo} />}
        {}
      </>
    );
  }
}

